int fac[MAX_N], n, T;

int getFac(int pos)
{
    if (pos <= 1e7)
        return fac[pos];
    int block_pos = (pos - 1e7) / block + 1;
    int fac_curt = magic_table[(block_pos << 1) - 1], fac_prefix = magic_table[(block_pos << 1)];
    for (int i = (block_pos - 1) * block + 1e7 + 1; i <= pos; i++)
        fac_curt = 1LL * fac_curt * i % mod, fac_prefix = (1LL * fac_prefix + fac_curt) % mod;
    return fac_prefix;
}

int main()
{
    scanf("%d", &T);
    for (int i = fac[0] = 1; i < MAX_N; i++)
        fac[i] = 1LL * fac[i - 1] * i % mod;
    for (int i = 1; i < MAX_N; i++)
        fac[i] = (1LL * fac[i] + fac[i - 1]) % mod;
    while (T--)
    {
        scanf("%d", &n);
        if (n < 3)
            puts("0");
        else
            printf("%lld\n", (1LL * (n - 3) * getFac(n - 1) % mod + 2) % mod);
    }
    return 0;
}